const express = require('express');
const multer = require('multer');
const csvParser = require('csv-parser');
const fs = require('fs');
const path = require('path');
const { User } = require('C:\Users\Yug\Desktop\Data-Driven Web Application\node_modules\models\User.js');

const router = express.Router();
const upload = multer({ dest: 'uploads/' });

router.post('/', upload.single('file'), async (req, res) => {
  const filePath = path.join(__dirname, req.file.path);
  const results = [];

  fs.createReadStream(filePath)
    .pipe(csvParser())
    .on('data', (data) => results.push(data))
    .on('end', async () => {
      try {
        await User.insertMany(results);
        fs.unlinkSync(filePath);  // Remove file after processing
        res.status(200).send({ message: 'File uploaded and data inserted' });
      } catch (error) {
        res.status(500).send({ message: 'Error inserting data', error });
      }
    });
});

module.exports = router;
